INSERT INTO cities(id,name,description,longitude,latitude,country_id,created_at,updated_at, thumbnail) VALUES (1,'Oran','Oran est une ville portuaire située sur la côte ouest de l''Algérie, au bord de la mer Méditerranée. Elle est la deuxième plus grande ville de l''ouest du pays, après la ville voisine d''Alger.\n\nLa ville est connue pour son architecture coloniale française, ses rues animées et son mélange de cultures arabes, berbères et européennes. Oran possède une histoire riche et diverse, ayant été tour à tour une colonie romaine, une ville arabe et une ville française sous la colonisation.\n\nParmi les attractions touristiques de la ville, on peut citer la mosquée Abdelhamid Ben Badis, le théâtre régional d''Oran, le Musée Ahmed Zabana et le Fort Santa Cruz. La ville est également réputée pour ses plages, dont la plage de Madagh et la plage de Les Andalouses.\n\nLa cuisine d''Oran est un mélange de saveurs locales et européennes, avec des plats populaires tels que le couscous, le tajine, le bourek et la paella. Les produits de la mer, tels que les crevettes et les poissons, sont également très appréciés dans la région.\n\nOran est une ville dynamique et cosmopolite qui offre une expérience unique de la culture algérienne, avec sa riche histoire, sa cuisine savoureuse et ses magnifiques paysages côtiers.',35.6969,-0.63374,1,'2023-05-08 17:28:47','2023-05-08 17:31:44','https://upload.wikimedia.org/wikipedia/commons/5/55/ORAN_%2C_VILLE_D%27ALGERIE.jpg');
INSERT INTO cities(id,name,description,longitude,latitude,country_id,created_at,updated_at, thumbnail) VALUES (2,'Constantine','Constantine est une ville située dans le nord-est de l''Algérie, sur une colline escarpée qui surplombe le fleuve Rhumel. C''est la troisième plus grande ville du pays après Alger et Oran.\n\nLa ville est souvent appelée la Ville des Ponts Suspendus en raison de ses nombreux ponts, dont le plus célèbre est le pont Sidi M''Cid qui relie les deux rives du Rhumel. Constantine est également connue pour son architecture historique, y compris la mosquée Emir Abdelkader, le palais du Bey et la casbah.\n\nLa cuisine de Constantine est un mélange de saveurs locales et méditerranéennes, avec des plats populaires tels que la chorba (soupe), le couscous, la m''chouekhia (plat à base de viande et de légumes) et les pâtisseries orientales.\n\nLes attractions touristiques de Constantine comprennent le musée national Cirta, qui abrite des artefacts de l''époque romaine, la basilique Sainte-Madeleine, l''université de Mentouri et le parc national Tassili n''Ajjer.\n\nConstantine est une ville dynamique et animée avec une histoire riche et une culture unique. Elle est un centre économique et culturel important dans la région, et offre une expérience unique de l''Algérie.',36.365,6.6147,1,'2023-05-08 17:32:12','2023-05-08 17:32:12','https://upload.wikimedia.org/wikipedia/commons/thumb/5/52/Gassiot.jpg/1920px-Gassiot.jpg');
INSERT INTO cities(id,name,description,longitude,latitude,country_id,created_at,updated_at, thumbnail) VALUES (3,'Tlemcen','Tlemcen est une ville historique située dans l''ouest de l''Algérie, près de la frontière avec le Maroc. Elle est souvent appelée la perle du Maghreb en raison de sa beauté et de son riche patrimoine culturel.\n\nLa ville est connue pour son architecture arabo-andalouse, ses mosquées et ses mausolées. Parmi les sites touristiques les plus populaires, on peut citer la mosquée Sidi Boumediene, la mosquée Mansourah, la citadelle de Tlemcen et le palais El Mechouar.\n\nLa cuisine de Tlemcen est un mélange de saveurs berbères et méditerranéennes, avec des plats populaires tels que le couscous, le tajine, la mhajeb (galettes farcies) et la baklawa.\n\nTlemcen est également réputée pour ses jardins, tels que le jardin d''El Mechouar, le jardin de la mosquée Sidi Boumediene et le jardin public de Tlemcen.\n\nLa ville est un centre important de l''artisanat, avec des artisans qui produisent des tapis, des bijoux, des poteries et des céramiques.\n\nTlemcen est une ville pleine de charme, riche en histoire et en culture. Elle offre une expérience unique de l''Algérie et est une destination touristique populaire pour ceux qui cherchent à explorer la beauté et la diversité de la région.',34.8828,-1.3167,1,'2023-05-08 17:32:51','2023-05-08 17:32:51','https://upload.wikimedia.org/wikipedia/commons/0/0e/Tlemcen_vue_g%C3%A9n%C3%A9rale_2.jpg');
INSERT INTO cities(id,name,description,longitude,latitude,country_id,created_at,updated_at, thumbnail) VALUES (4,'Alger','Alger est la capitale de l''Algérie et la plus grande ville du pays, située sur la côte nord de la mer Méditerranée. Elle est souvent appelée La Blanche en raison de ses bâtiments blancs et de son architecture coloniale française.\n\nLa ville est riche en histoire, avec une architecture unique qui reflète son passé de ville romaine, puis de ville arabe et enfin de ville française sous la colonisation. Parmi les attractions touristiques les plus populaires, on peut citer la Casbah d''Alger, classée au patrimoine mondial de l''UNESCO, la basilique Notre-Dame d''Afrique, le musée national des antiquités et des arts islamiques et le palais des Raïs.\n\nLa cuisine algéroise est un mélange de saveurs arabes et françaises, avec des plats populaires tels que la chorba (soupe), le couscous, la tajine et les pâtisseries orientales.\n\nAlger est également connue pour ses plages, telles que la plage de Sidi Fredj et la plage de Zéralda, ainsi que pour ses parcs et jardins, tels que le jardin d''Essai et le parc de la Liberté.\n\nLa ville est un centre culturel important, avec une scène artistique dynamique et de nombreux festivals et événements tout au long de l''année.\n\nAlger est une ville passionnante et animée, offrant une expérience unique de la culture algérienne, avec son architecture historique, sa cuisine savoureuse et ses magnifiques paysages côtiers.',36.7764,3.0586,1,'2023-05-08 17:33:28','2023-05-08 17:33:28','https://i.pmdstatic.net/GEO/var/geo/storage/images/voyages/vos-voyages-de-reve/algerie-voyage-alger/algerie-voyage-alger/434096-1-fre-FR/panorama-d-alger.jpg');
INSERT INTO cities(id,name,description,longitude,latitude,country_id,created_at,updated_at, thumbnail) VALUES (5,'Annaba','Annaba est une ville côtière située dans le nord-est de l''Algérie, sur la mer Méditerranée. Elle est la quatrième plus grande ville du pays et est connue pour sa beauté naturelle, son patrimoine culturel et son riche passé historique.\n\nLa ville est célèbre pour ses plages, notamment la plage de la Baie des Corailleurs et la plage de St Cloud, qui attirent de nombreux visiteurs. Elle est également connue pour ses ruines romaines, notamment le site archéologique de l''ancienne ville de Hippone, classé au patrimoine mondial de l''UNESCO, qui abrite des vestiges romains tels que des temples, des théâtres et des thermes.\n\nLa cuisine annabie est un mélange de saveurs locales et méditerranéennes, avec des plats populaires tels que le couscous, le tajine, le pain traditionnel appelé khobz dar et les pâtisseries orientales.\n\nLa ville est un centre culturel important avec de nombreux musées, centres d''art et de culture, festivals et événements tout au long de l''année. Le musée d''Hippone est un lieu de choix pour en apprendre davantage sur l''histoire et la culture de la ville et de la région.\n\nAnnaba est une ville dynamique et animée, offrant une expérience unique de l''Algérie avec ses belles plages, ses sites historiques et son patrimoine culturel riche et diversifié.',3.0586,7.7667,1,'2023-05-08 17:33:54','2023-05-08 17:33:54', 'https://upload.wikimedia.org/wikipedia/commons/5/52/Gassiot.jpg');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (3,'Citadelle d’el méchouar','El-Méchouar est l''ancienne citadelle construite par Yaghmourassen Ibn Ziyan (1234/1235-1282), transformée plus tard en résidence officielle des Banou Abd-el-oued les rois de Tlemcen. Mohammed El-Tinnisi (de Ténès) parle d''édifices extraordinaires, de hauts pavillons et des jardins luxuriants. El-Idrissi faisant référence au XVIe siècle cite la magnificence architecturelle des édifices. Au centre de Tlemcen, se dresse une enceinte fortifiée de forme quadrupède d’une superficie d''environ 4 hectares. Ce mur de cinq mètres de haut, construit à l''origine en pisé, mais restauré en pierres à l''époque coloniale, est surmonté d''un passage pour les soldats.',34.881670,-1.308781,'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/17/6b/16/b3/img-20190505-125551955.jpg?w=1200&h=-1&s=1;https://i0.wp.com/harba-dz.com/wp-content/uploads/2020/10/Palais-royal-du-Mechouar-1-Tlemcen.jpg?fit=1200%2C885&ssl=1','Centre ville, Tlemcen , Tlemcen',NULL,NULL,'http://www.tlemcen-dz.com/endroits-visiter/el-mechouar-tlemcen.html','10:00 am to 16:00 pm',200.00,'Monument',3,'2023-05-09 11:33:51','2023-05-09 13:29:27');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (11,'La bibliothèque municipale d''Oran','la Bibliothèque municipale d''Oran était à l’origine la cathédrale du Sacré-Cœur. C’était un édifice religieux catholique de style romano-byzantin situé à Oran. L''église a été construite de 1903 à 1913 par l''architecte Albert Ballu. Elle a été transformée en bibliothèque régionale en 1984, puis en bibliothèque municipale en 1996',35.696299,-0.643999,'https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-455-__-1647422711553_Site_2022-03-16DSC_6480%20cathedrale%20oran%20(2).JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-455-__-1647422820782_Site_2022-03-16DSC_6480%20cathedrale%20oran%20(19).JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-455-__-1647422973821_Site_2022-03-16DSC_6480%20cathedrale%20oran%20(17).JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-455-__-1647423275668_Site_2022-03-16DSC_6480%20cathedrale%20oran%20(15).JPG&w=1920&q=75','Rue maitre ali boumendjel, Oran , Oran',NULL,NULL,'https://algeriatours.dz/fr/site/la-bibliothe%CC%80que-municipale-d-oran-3101-fr','10:00 am to 16:00 pm',0.00,'Monuments',1,'2023-05-09 12:59:55','2023-05-09 13:27:01');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (4,'La mosquée d’alger','La grande mosquée d''Alger est située au cœur de la baie de la capitale, en bordure de mer. Elle occupe une superficie de 31 hectares, comprenant 11 bâtiments. C''est un ensemble religieux, culturel et scientifique. Il contient principalement dans sa façade nord la salle de prière, le phare et la place, tandis que sa façade sud renferme des installations scientifiques et culturelles : la maison du Coran, la bibliothèque et des annexes. Le site en lui-même porte un symbolisme historique avéré sur le plan du lieu d’implantation. En effet, la municipalité de Mohammadia, portait durant la période coloniale, le nom du cardinal Lavigerie, qui a mené des campagnes de prosélytisme visant à effacer l''identité et la religion du peuple algérien.',36.785377,3.063488,'https://www.algerie360.com/wp-content/uploads/2022/03/Sans-titre-7.jpg;https://upload.wikimedia.org/wikipedia/commons/f/fe/Grande_Mosqu%C3%A9e_d%27Alger.jpg','Mohammadia , Mohammadia , Alger',NULL,NULL,'https://algeriatours.dz/fr/site/la--mosque%CC%81e-d%E2%80%99alger--1629-fr','tous les jours',0.00,'Mosque',4,'2023-05-09 12:15:09','2023-05-09 13:29:14');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (1,'Mansourah','La Mosquée de Mansourah est située dans la ville de Mansourah, construite par les rois mérinides. Les fouilles ont permis de retracer les fondations de la mosquée édifiée par le sultan Abu Yaqoub en 1303, et dont le sultan Abu al-Hasan a redécoré la porte principale en 1336. On y accède par 13 portes. Elle est entourée d''une muraille et avait une cour carrée où trônait une fontaine en son milieu. Tout autour se trouvent des galeries triangulaires sur les côtés Ouest et Est formant un prolongement des nefs de la salle de prière et un portique côté nord.',34.878507,-1.348165,'https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-544-__-1647446090469_Site_2022-03-16IMG_0020.JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-544-__-1647446551027_Site_2022-03-16fichier_produit_21.jpg&w=1920&q=75; https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-544-__-1647446464899_Site_2022-03-16fichier_produit_334.jpg&w=1920&q=75','Mansourah, Mansourah , Tlemcen','+213 661 91 56 54',NULL,'http://www.tlemcen-dz.com/endroits-visiter/vestiges-mansourah-tlemcen.html','10:00 am to 16:00 pm',100.00,'Monuments',3,'2023-05-08 17:58:25','2023-05-09 13:29:45');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (8,'Mosquée sidi boumediene','La mosquée Sidi Boumediene est située dans la cité El-Eubbad, qui, comme d''autres quartiers du Maghreb antique, se caractérise par ses étroites venelles ascendantes. Le minaret de la mosquée Sidi Boumediene est considéré comme le deuxième minaret des Mérinides à Tlemcen. La construction de la mosquée et de son minaret ont été ordonnée par le sultan Abou El-Hassan El-Marini en 739 H / 1338, comme l''indique l’épigraphe incrustée dans le cadre de l''entrée principale de la mosquée.',34.878259,-1.289689,'https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-607-__-1647515553048_Site_2022-03-17IMG_9489.JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-607-__-1647515053906_Site_2022-03-17IMG_9413.JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-607-__-1647515113007_Site_2022-03-17IMG_9414.JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-607-__-1647515403632_Site_2022-03-17IMG_9526.JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-607-__-1647515287196_Site_2022-03-17IMG_9504.JPG&w=1920&q=75','Quartier el eubbad, Tlemcen , Tlemcen',NULL,NULL,'https://algeriatours.dz/fr/site/mosque%CC%81e-sidi-boumediene-1301-fr','tous les jours',0.00,'Mosques',3,'2023-05-09 12:34:14','2023-05-09 13:27:53');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (7,'Plateau lalla setti','Le plateau de part sa position géographique et ses 660m d’altitude par rapport niveau de la mer est une véritable attraction touristique qui attire de nombreux touristes de différentes régions du pays. Lalla est le nom d’une vertueuse bienfaitrice qui a tant donnée à la ville et qu’en reconnaissance les habitants l’y enterrèrent et firent de sa tombe un mausolée qui devient avec le temps, à son tour une autre attraction.\nLorsque vous vous y rendez, des plaines verdoyantes vous reçoivent et de ses falaises vous contemplerez la ville. Vous trouverez également de beaux et anciens bâtiments et des espaces spacieux fréquentés par les habitants pour profiter des soirées de Ramadan loin des bruits de la ville. Le plateau possède un parc touristique équipé de toutes les commodités et des hôtels à même de répondre à tous les goûts et aux clients les plus exigeants.',34.868798,-1.319027,'https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-1018-__-1648122546228_Site_2022-03-24city1.jpg&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-1018-__-1648122641465_Site_2022-03-24city2.jpg&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-1018-__-1648123011863_Site_2022-03-24city4.jpg&w=1920&q=75','Route nationale n27, Tlemcen , Tlemcen',NULL,NULL,'https://algeriatours.dz/fr/site/plateau-lalla-setti-1301-fr','tous les jours',0.00,'Parks',3,'2023-05-09 12:31:43','2023-05-09 13:28:14');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (6,'La Grande mosquée de Tlemcen','La Grande Mosquée de Tlemcen a été construite en 1136 par Youssef bin Tachfine, le premier calife de la dynastie almoravide. Le minaret quant à lui, a été construit en 1236 par Yaghmourasen ibn Zayan en pierre, plâtre sculpté et perforé, porcelaine et bois.',34.884063,-1.310778,'https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-588-__-1647510668395_Site_2022-03-17IMG_9598.JPG&w=1920&q=75; https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-588-__-1647512031694_Site_2022-03-17IMG_9562.JPG&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-588-__-1647511448705_Site_2022-03-17IMG_9565.JPG&w=1920&q=75','Centre ville, Tlemcen , Tlemcen',NULL,NULL,'https://algeriatours.dz/fr/site/grande-mosque%CC%81e-1301-fr','tous les jours',0.00,'Mosques',3,'2023-05-09 12:26:44','2023-05-09 13:28:33');
INSERT INTO places(id,name,description,longitude,latitude,images,address,phone,email,website,opening_hours,price,category,city_id,created_at,updated_at) VALUES (16,'Basilique saint augustin','L''église est construite sur la colline d’Hippone, avec entre autres un monastère, une maison pour personnes âgées et une bibliothèque construite à l''initiative de l''évêque Dupuch, qui a envisagé la possibilité de propager la religion chrétienne dans la zone qu''il avait connue pendant la période de Saint-Augustin. Cette église est considérée comme un hommage à la mémoire d''Augustin, qui était l''évêque de Hippone de 395 jusqu''à sa mort en 430. Elle a été construite vers la fin du XIXe siècle. Son architecture est une combinaison du style arabo-musulman et romano- byzantin. Elle a été restaurée entre 2010 et 2013. Ce monument se trouve sur le parcours touristique religieux dédié à Saint Augustin et étendu aux wilayas de Guelma et du Souk-Ahras, dans les zones où Saint Augustin était actif avant de s''installer à Hippone.',36.881966,7.744686,'https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-626-__-1647525625865_Site_2022-03-17%D8%B5%D9%88%D8%B1%D8%A9%20%D8%A7%D9%84%D9%83%D9%86%D8%B3%D9%8A%D8%A9%20%D9%85%D9%86%20%D8%A7%D9%84%D8%AE%D8%A7%D8%B1%D8%AC%2002.jpg&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-626-__-1647525279761_Site_2022-03-17%D8%B5%D9%88%D8%B1%D8%A9%20%D8%A7%D9%84%D9%83%D9%86%D8%B3%D9%8A%D8%A9%20%D9%85%D9%86%20%D8%A7%D9%84%D8%AF%D8%A7%D8%AE%D9%84%2002.jpg&w=1920&q=75;https://algeriatours.dz/_next/image?url=https%3A%2F%2Falgeriatours.dz%2Fadmin%2Fapi%2Ffile%2Fdownload%2FSites-__-626-__-1647524674252_Site_2022-03-17%D8%B5%D9%88%D8%B1%D8%A9%20%D8%A7%D9%84%D9%83%D9%86%D8%B3%D9%8A%D8%A9%20%D9%85%D9%86%20%D8%A7%D9%84%D8%AE%D8%A7%D8%B1%D8%AC%2003.jpg&w=1920&q=75','Chemin de la basilique st augustin, annaba, Annaba , ANNABA',NULL,